sp_widget.client_script:
function($rootScope,$scope) {
  /* widget controller */
  var c = this;
	var index;
	c.selectItem = function(idx){
		index = idx;
		var noteID = c.data.notes[idx].sys_id;
		$rootScope.noteID = noteID;
		$rootScope.$emit('showBody', noteID);
	}
	$rootScope.$on('refreshNote', function (event, data){
		console.log('refresh value '+ data.note);
		c.data.notes[index].note = data.note;
		c.data.notes[index].title = data.title;
	})
}

sp_widget.script:
(function() {
  /* populate the 'data' object */
  data.notes = [];
	var noteGR = new GlideRecord('x_snc_createnotes_note');
	noteGR.addQuery('user', gs.getUser().getID());
	noteGR.orderByDesc('sys_create_on');
	noteGR.query();
	while (noteGR.next()){
		var noteObj = {};
		$sp.getRecordDisplayValues(noteObj, noteGR, 'title,note,sys_id');
		noteObj.note = noteGR.getValue('note').slice(0,20);
		data.notes.push(noteObj);
	}

})();
