sp_widget.client_script:
function($rootScope, $scope) {
  /* widget controller */
  var c = this;
	$rootScope.$on('showBody', function(event,data){
		c.server.get({
			noteID: $rootScope.noteID,
			action: 'showBody'
		}).then(function (r){
			c.data.noteID = r.data.noteID;
			c.data.note = r.data.note;
			c.data.title = r.data.title;
		});
	});

	c.updateNote = function (update){
		c.server.get({
			action: 'updateBody',
			noteID: $rootScope.noteID,
			title: c.data.title,
			note: c.data.note
		}).then(function (r){
			//console.log('updated value '+ r.data.note.title);
			$rootScope.$emit('refreshNote', r.data.note);
		});
	}
}

sp_widget.script:
(function() {
  /* populate the 'data' object */
	if (input && input.noteID){
		var noteBody = new GlideRecord('x_snc_createnotes_note');
		noteBody.addQuery('sys_id',input.noteID);
		noteBody.query();
		data.note = {};
		if (input.action == 'showBody'){
			while (noteBody.next()){
			//data.note = {};
			//$sp.getRecordValues(data.note,noteBody,'note,title,sys_id');
			//console.log('called by input');
			data.note = noteBody.getValue('note');
			data.noteID = noteBody.getValue('sys_id');
			data.title = noteBody.getValue('title');
		}
		}
		if (input.action == 'updateBody'){
			while (noteBody.next()){
				noteBody.title = input.title;
				noteBody.note = input.note;
				noteBody.update();
				$sp.getRecordValues(data.note,noteBody,'note,title,sys_id');
			}
		}
	}

})();
