sp_widget.client_script:
function($rootScope,$scope) {
	/* widget controller */
	var c = this;
	c.selectItem = function(idx) {
		console.log('Notes client '+ c.data.notes)
		var id = c.data.notes[idx].sys_id;
		console.log('Note ID: ' + id);
		$rootScope.noteID = id;
		$rootScope.$emit('selectNote', id);

	}
			$rootScope.$on('updateNoteList', function(event,data) {
			c.server.get({
			action: 'updateNoteList',
			noteID: c.data.noteID
		}).then(function(r) {
			c.data.title = r.data.notes[0].title;
			c.data.note = r.data.notes[0].note;
			c.data.noteID = r.data.notes[0].sys_id;
			console.log('notes array value is:' + r.data.notes[0].title);
			console.log( c.data.title);
			console.log('notes array value is:' + r.data.notes[1].title);	
		});
		});
}

sp_widget.script:
(function() {
//create an array to populate without notes
console.log('server script from Notes List ' + input);
data.notes = [];
var noteGR = new GlideRecord('x_snc_createnotes_note');
noteGR.addQuery('user', gs.getUser().getID());
noteGR.orderByDesc('sys_created_on');
noteGR.query();
while (noteGR.next()) {
var noteObj = {};
//use service portal helper method to get some display values
$sp.getRecordDisplayValues(noteObj, noteGR, 'number,title,note,sys_id');
//get the first 20 characters of the description
noteObj.note = noteGR.getValue('note').slice(0,20);
//push the populated obj into the array
data.notes.push(noteObj);
}
if (input && input.action == 'updateNoteList'){
	console.log('server script from Notes List updateNoteList '+ input)
}
})();
